import { useDispatch, useSelector } from "react-redux";
import {add,remove} from "../redux/Slices/cartSlice";

const Product = ({ post }) => {
  const { cart } = useSelector((state) => state);

  const dispatch = useDispatch();

  const addToCart = () => {
    dispatch(add(post));
  };

  const removeFromCart = () => {
    dispatch(remove(post.id));
  };
  return (
    <div className="flex flex-col items-center justify-between hover:scale-110 transition duration-300  ease-in gap-3 p-4 mt-10 ml-5 rounded-xl  ">
      <div>
        <p className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1 ">{post.title}</p>
      </div>
      <div>
        <p className="w-40 text-gray-400 font-normal text-[10px] text-left">{post.description.splite(" ").slice(0,10).join(" ")+"..."}</p>
      </div>
      <div>
        <img src={post.image} />
      </div>
      <div>
        <p>{post.price}</p>
      </div>
      <>
        {cart.some((p) => p.id == post.id) ? (
          <button onClick={removeFromCart}>Remove Item</button>
        ) : (
          <button onClick={addToCart}>Add to Cart</button>
        )}
      </>
    </div>
  );
};

export default Product;
